package ldap;import org.owasp.encoder.Encode;import javax.naming.NamingEnumeration;import javax.naming.NamingException;import javax.naming.directory.SearchControls;import javax.naming.directory.SearchResult;import javax.naming.directory.DirContext;public class LDAPTest4 {  public boolean test(String user, String pass, String domainName, DirContext ctx)      throws NamingException {    String userUnsafe = null;    userUnsafe = user;    user = "{0}" + "_USER";    String saltedPassword = "{1}" + "%!";    String filter = "(&(uid=" + user + ")(userPassword=" + saltedPassword + "))";    NamingEnumeration<SearchResult> results =        ctx.search("ou=system", filter, new String[] {userUnsafe, pass}, new SearchControls());    return results.hasMore();  }}